import React from "react"
import "./Header.css"


let base_icon_link = "https://raw.communitydragon.org/latest/plugins/rcp-be-lol-game-data/global/default/v1/profile-icons/"

export default function Header(props) {
    // const [username, setUserName] = React.useState("")
    // const [icon_id, setIcon_id] = React.useState(0)

    const [searchAccount, updateSearchAccount] = React.useState({
        username: "",
        icon_id: 0,
        region: ""
    })

    const [account, updateAccount] = React.useState({
        username: "",
        icon_id: 0,
        region: ""
    })

    // API call for Icon and Username display
    // React.useEffect(() => {
    //     fetch("https://" + props.region + ".api.riotgames.com/lol/summoner/v4/summoners/by-name/Mateo?api_key=" + props.api_key)
    //     .then(res => res.json())
    //     .then(data => updateAccount(prev => ({
    //         ...prev,
    //         icon_id: data.profileIconId
    //     })))
    // }, [0])

    console.log(searchAccount)
    function handleSearchChange(event) {
        const {name, value} = event.target
        updateSearchAccount(prev => {
            return {
                ...prev,
                [name]: value
            }
        })
    }


    function handleSubmit(event) {
        updateAccount({
            ...searchAccount,
        })

        React.useEffect( () => {
            fetch("https://" + account.region + ".api.riotgames.com/lol/summoner/v4/summoners/by-name/" + account.username + "?api_key" + props.api_key)
            .then(res =>res.json())
            .then(data => updateAccount(prev => ({
                ...prev,
                icon_id: data.profileIconId
            })))
        })
    }

    return (
        <div className="header"
            onSubmit={handleSubimt}>
            <form className="header--search">
                <select
                    id="region"
                    name="region"
                    onChange={handleSearchChange}
                >
                    <option value="">Region</option>
                    <option value="na1">NA</option>
                    <option value="euw1">EUW</option>
                    <option value="eun1">EUNE</option>
                </select>


                <input
                    type="text"
                    placeholder="Summoner Name"
                    onChange={handleSearchChange}
                    name="username"
                    value={searchAccount.username}
                />
                <button>Search</button>
            </form>


            <div className="header--user--section">
                <img className="header--user--icon" src={base_icon_link + searchAccount.icon_id + ".jpg"} alt={"icon_id=" + searchAccount.icon_id} />
                <h1 className="header--user--name"> {searchAccount.username}</h1>
            </div>

        </div>
    )


}
