import React from "react"
import "./Header.css"


export default function Header(props) {
    const [username, setUserName] = React.useState("")
    const [icon_id, setIcon_id] = React.useState(0)
    const [region, setRegion] = React.useState("")

    // API call for Icon and Username display
    React.useEffect(() => {
        fetch("https://" + props.region + ".api.riotgames.com/lol/summoner/v4/summoners/by-name/Mateo?api_key=" + props.api_key)
        .then(res => res.json()).then(data => {
            setUserName(data.name)
            setIcon_id(data.profileIconId)
        })
    })

    const base_icon_link = "https://raw.communitydragon.org/latest/plugins/rcp-be-lol-game-data/global/default/v1/profile-icons/"
    return (
        <div className="header">
            <div className="header--search">
                <select>
                    <option value="">Region</option>
                    <option value="na1">NA</option>
                    <option value="euw1">EUW</option>
                    <option value="eun1">EUNE</option>
                </select>

            </div>
            <div className="header--user--section">
                <img className="header--user--icon" src={base_icon_link + icon_id + ".jpg"} alt={"icon_id=" + icon_id} />
                <h1 className="header--user--name"> {username}</h1>
            </div>

        </div>
    )


}
